import React, { useState } from 'react';
import axios from 'axios';

function Contact({ profile }) {
  const [form, setForm] = useState({ name: '', email: '', subject: '', message: '' });
  const [status, setStatus] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('/api/contact', form);
      setStatus(res.data.message);
      setForm({ name: '', email: '', subject: '', message: '' });
    } catch (error) {
      setStatus('Error sending message');
    }
  };

  return (
    <section id="contact" className="contact">
      <div className="container">
        <h2>Let's Work Together</h2>
        <p className="subtitle">Available for freelance projects</p>
        <div className="contact-content">
          <div className="contact-info">
            <div className="contact-item">
              <h3>ðŸ“§ Email</h3>
              <a href={`mailto:${profile.email}`}>{profile.email}</a>
            </div>
            <div className="contact-item">
              <h3>ðŸ’¼ LinkedIn</h3>
              <a href={profile.linkedin} target="_blank" rel="noreferrer">LinkedIn Profile</a>
            </div>
            <div className="contact-item">
              <h3>ðŸ’» GitHub</h3>
              <a href={profile.github} target="_blank" rel="noreferrer">GitHub Profile</a>
            </div>
            <div className="contact-item">
              <h3>ðŸ’¬ WhatsApp</h3>
              <a href={profile.whatsapp} target="_blank" rel="noreferrer" className="btn-whatsapp">Message</a>
            </div>
          </div>
          <form onSubmit={handleSubmit}>
            <input type="text" placeholder="Name" value={form.name} onChange={e => setForm({...form, name: e.target.value})} required />
            <input type="email" placeholder="Email" value={form.email} onChange={e => setForm({...form, email: e.target.value})} required />
            <input type="text" placeholder="Subject" value={form.subject} onChange={e => setForm({...form, subject: e.target.value})} required />
            <textarea placeholder="Message" value={form.message} onChange={e => setForm({...form, message: e.target.value})} required></textarea>
            <button type="submit" className="btn btn-primary">Send Message</button>
            {status && <p className="status">{status}</p>}
          </form>
        </div>
      </div>
    </section>
  );
}

export default Contact;
